        Customer[] customers = new Customer[4];
        customers[0]= new Customer("Trần Thanh Trí","03/02/1995","Quảng Trị","data:image/jpeg;base64,");
        customers[1]= new Customer("Lê Thị Tài","03/03/1995","Xứ sở nem chua","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS6h4Lq9bSgjbXfGW-EbUDGIanRQ7QMU9eYAg&usqp=CAU");
        customers[2]= new Customer("Lê Thị Diệp","04/02/1995","xứ sở mỳ quảng","data:image/jpeg;base64,");
        customers[3]= new Customer("Lê Thị Hậu","03/02/1994","xứ sở bánh canh","data:image/jpeg;base64,");
        request.setAttribute("listCustomer",customers);
